import React, { Component } from 'react';
import { Button, Row, Col } from 'react-bootstrap';
import { Link } from "react-router-dom";
import { GoogleApiWrapper } from 'google-maps-react';
import Map from '../../api/Map';
import './index.css';
import cities from '../../asset/dict';

class Index extends Component {
    state = {
        latitude:'',
        longitude:'',
    }

    handleChange = e => {
        e.preventDefault();
        const { name, value } = e.target;
        this.setState({ [name]: value }, () => console.log(this.state));
    };

    confirm =()=> {
        window.location.href=`/main/:(${this.state.latitude},${this.state.longitude})`;
    }

    render(){
        return (
            <>
            <Row>
                <Col>
                    <div className="latitude">
                        <label htmlFor="latitude">latitude</label>
                        <input
                            placeholder="latitude"
                            type="float"
                            name="latitude"
                            onChange={this.handleChange}
                        />
                    </div>
                </Col>
                <Col>
                    <div className="longitude">
                        <label htmlFor="longitude">longitude</label>
                        <input
                            placeholder="longitude"
                            type="float"
                            name="longitude"
                            onChange={this.handleChange}
                        />
                    </div>
                </Col>
                <Col>
                <Link to='/details' />
                </Col>
            </Row>
            <Row>
                <Col lg="10">
                    <Map 
                        latitude={this.state.latitude}
                        longitude={this.state.longitude}
                    />
                </Col>
                <Col lg="2">
                    <Button className="button2" onClick={this.confirm}>Confirm</Button>
                </Col>
            </Row>



            </>
        )
    }

}

export default GoogleApiWrapper({
    apiKey: 'AIzaSyDTvim5iSgu4FyajIK4ZOXS7CgSqapq85g'
  })(Index);
