import React, { Component } from 'react';
import { Button, Row, Col, InputGroup, FormControl } from 'react-bootstrap';
import { Link } from "react-router-dom";
import { GoogleApiWrapper } from 'google-maps-react';
import Map from '../../api/Map';
import './index.css';
import cities from '../../asset/dict';

class Index extends Component {
    state = {
        latitude:'',
        longitude:'',
    }

    handleChange = e => {
        e.preventDefault();
        const { name, value } = e.target;
        this.setState({ [name]: value }, () => console.log(this.state));
    };

    confirm =()=> {
        window.location.href=`/main/:(${this.state.latitude},${this.state.longitude})`;
    }

    render(){
        return (
            <>
            <h2>This is the title</h2>
            <br />
            <Row>
                <Col lg="3">
                    <div className="latitude">
                        <label htmlFor="latitude">latitude</label>
                        <input
                            placeholder="Ex:43.6532"
                            type="float"
                            name="latitude"
                            onChange={this.handleChange}
                        />
                    </div>
                </Col>
                <Col lg="3">
                <InputGroup size="sm" className="mb-3">
                    <InputGroup.Prepend>
                    <InputGroup.Text id="inputGroup-sizing-sm">Small</InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl aria-label="Small" aria-describedby="inputGroup-sizing-sm" />
                </InputGroup>
                    <div className="longitude">
                        <label htmlFor="longitude">longitude</label>
                        <input
                            placeholder="Ex:-79.3832"
                            type="float"
                            name="longitude"
                            onChange={this.handleChange}
                        />
                    </div>
                </Col>
                <Col>
                <Link to='/details' >City Details</Link>
                </Col>
            </Row>
            <Row>
                <Col lg="11">
                    <Map 
                        latitude={this.state.latitude}
                        longitude={this.state.longitude}
                    />
                </Col>
                <Col lg="1">
                    <Button className="button" onClick={this.confirm}>Confirm</Button>
                </Col>
            </Row>



            </>
        )
    }

}

export default GoogleApiWrapper({
    apiKey: 'AIzaSyDTvim5iSgu4FyajIK4ZOXS7CgSqapq85g'
  })(Index);
